LyteComponent.registerHelper('lyteUiSetOffset',function(radius,stroke,value){
	var r = parseInt(radius)-parseInt(stroke)/2;
	var strokeDash =  2 * 3.14159 * r;
	return strokeDash * (1 - parseInt(value)/100);
});
