LyteComponent.registerHelper("lyteRgbToHex",function(item){
	var valArray = item.substring(4,item.length-1).split(",");
	var hexValue = "#";
	for(var i=0;i<3;i++){
		var val = parseInt(valArray[i]).toString(16).toUpperCase();
		if(val.length < 2){
			val = "0"+val;
		}
		hexValue += val;
	}
	return hexValue;
});
